import playIcon from "./play.svg";
import "./styles/video.scss"; // 这里有个动画演示，可以用来学习 CSS：《CSS制作播放、暂停按钮》https://codepen.io/chriscoyier/full/lotjh

export default {
  name: 'lbp-video',
  props: {
    src: {
      type: String,
      default: "",
      editor: {
        type: 'a-input',
        label: '视频url',
        prop: {
          type: 'textarea'
        },
        extra: function extra(h) {
          return h("a", {
            "attrs": {
              "href": 'https://github.com/ly525/luban-h5/issues/85',
              "target": '_blank'
            }
          }, ["\u6559\u7A0B(Tutorial)"]);
        }
      }
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  watch: {
    src: function src() {
      this.appendIframe();
    }
  },
  mounted: function mounted() {
    this.appendIframe();
  },
  methods: {
    appendIframe: function appendIframe() {
      if (this.src) {
        this.$el.innerHTML = this.src;
      }
    }
  },
  render: function render(h) {
    var style = this.disabled ? {
      'pointer-events': 'none'
    } : {};
    return h("div", {
      "class": "lbc-video",
      "style": style
    }, [this.disabled ? h("video", {
      "attrs": {
        "playsinline": "true",
        "webkit-playsinline": "",
        "width": "100%",
        "height": "100%",
        "poster": playIcon
      }
    }, [h("source", {
      "attrs": {
        "type": "video/mp4"
      }
    })]) : h("div")]);
  },
  editorConfig: {
    components: {}
  }
};
