export default {
  text: ({ defaultValue = '按钮', label = '按钮文字' } = {}) => ({
    type: String,
    default: defaultValue,
    editor: {
      type: 'a-input',
      label,
      require: true
    }
  }),
  text1: ({ defaultValue = '微信', label = '按钮文字' } = {}) => ({
    type: String,
    default: defaultValue,
    editor: {
      type: 'a-input',
      label,
      require: true
    }
  }),
  text2: ({ defaultValue = '电话', label = '按钮文字' } = {}) => ({
    type: String,
    default: defaultValue,
    editor: {
      type: 'a-input',
      label,
      require: true
    }
  }),
  userName: ({ defaultValue = '姓名', label = '标题文字' } = {}) => ({
    type: String,
    default: defaultValue,
    editor: {
      type: 'a-input',
      label,
      require: true
    }
  }),
  phoneNum: ({ defaultValue = '电话', label = '标题文字' } = {}) => ({
    type: String,
    default: defaultValue,
    editor: {
